mapper.select:
SELECT * from tbl_sys_permission WHERE id in
(
select pid from tbl_sys_role_permission WHERE rid in
(
select rid from tbl_sys_user_role INNER JOIN tbl_sys_role on tbl_sys_user_role.rid = tbl_sys_role.id where uid = #{uid} and status = 1
)
) and status = 1 and type = 1 and pid = #{pid}
